import axios from "axios";

export const handleLogin = (email, pass) => {

    const loginPayload = {
      email: email,
      password: pass
    }
    axios.post("http://localhost:8080/api/v1/auth/login", loginPayload)
      .then(response => {
        const token  =  response.data.token;
        localStorage.setItem("token", token);
      })
      .catch(err => console.log(err));
  };

  export const handleSignUp = (lastName, firstName, email, pass) => {

    const signupPayload = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: pass
    }

    axios.post("http://localhost:8080/api/v1/auth/register", signupPayload)
      .catch(err => console.log(err));
  };
