import React, { useState } from 'react'; 
import axios from 'axios'; 
import { useNavigate } from 'react-router-dom'; 
import { 
	MDBContainer, 
	MDBInput, 
	MDBBtn, 
} from 'mdb-react-ui-kit'; 

function Login() { 
	const [username, setUsername] = useState(''); 
	const [password, setPassword] = useState(''); 
	const [error, setError] = useState(''); 
	const history = useNavigate(); 

	const handleLogin = async () => { 
		try { 
			if (!username || !password) { 
				setError('Please enter both username and password.'); 
				return; 
			} 

			const response = await axios.post('http://localhost:8081/auth/signin', { username, password }); 
			console.log('Login successful:', response.data); 
			history('/dashboard'); 
		} catch (error) { 
			console.error('Login failed:', error.response ? error.response.data : error.message); 
			setError('Invalid username or password.'); 
		} 
	}; 

	return ( 
		<div className="vh-100 d-flex align-items-center col justify-content-center "> 
			<div className="border rounded-lg row h-100" style={{ width: '500px', height: 'auto' }}> 

			</div> 
			<div class="d-flex align-items-center justify-content-center min" style={{height: "50vh"}}>
				<button class="btn btn-outline-success mx-2">Register</button>
				<h2 className="mb-4 text-center">Login Page</h2> 
				<h2 className="mb-4 text-center">Login Page</h2> 
					<MDBInput wrapperClass='mb-4' placeholder='Email address' id='email' value={username} type='email' onChange={(e) => setUsername(e.target.value)} /> 
					<MDBInput wrapperClass='mb-4' placeholder='Password' id='password' type='password' value={password} onChange={(e) => setPassword(e.target.value)} /> 
					{error && <p className="text-danger">{error}</p>} {/* Render error message if exists */} 
					<button className="mb-4 d-block btn-primary" style={{ height:'50px',width: '100%' }} onClick={handleLogin}>Sign in</button> 
					<div className="text-center"> 
						<p>Not a member? <a href="/signup" >Register</a></p> 
					</div> 
				<button class="btn btn-outline-primary mx-2" style={{width:"123.44px"}}>Login</button>
			</div>

		</div> 
	); 
} 

export default Login;
