import axios from "axios";

export const handleLogin = (email, pass) => {

    const loginPayload = {
      email: email,
      password: pass
    }

    axios.post("http://localhost:8080/api/v1/auth/login", loginPayload)
      .then(response => {
        const token  =  response.data.token;
        localStorage.setItem("token", token);
        return true
      })
      .catch(err => {
        if (err.response) {
          throw new Error(error.response.data.message || 'Login failed. Please try again.');
        } else if (error.request) {
            throw new Error('No response from the server. Please check your network connection.');
        } else {
            throw new Error('An unexpected error occurred. Please try again.');
        }}
      );

  };

  export const handleSignUp = (firstName,lastName, email, pass) => {

    const signupPayload = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: pass
    }

    axios.post("http://localhost:8080/api/v1/auth/register", signupPayload)
      .catch(err => console.log(err));
  };
