import axios from "axios";

export const handleLogin = async (email, pass) => {

    const loginPayload = {
      email: email,
      password: pass
    }

    try {
        const response = await axios.post("http://localhost:8080/api/v1/auth/login", loginPayload);
        const token  =  response.data.token;
        localStorage.setItem("token", token);
    } catch (error) {
        if (error.response) {
            throw new Error(error.response.data.message || 'Login failed. Please try again.');
        } else if (error.request) {
            throw new Error('No response from the server. Please check your network connection.');
        } else {
            throw new Error('An unexpected error occurred. Please try again.');
        }
    }
};

  export const handleSignUp = async (firstName,lastName, email, pass) => {

    const signupPayload = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: pass
    }

    try {
        await axios.post("http://localhost:8080/api/v1/auth/register", signupPayload);
    } catch (error) {
        if (error.response) {
            throw new Error(error.response.data.message || 'Signup failed. Please try again.');
        } else if (error.request) {
            throw new Error('No response from the server. Please check your network connection.');
        } else {
            throw new Error('An unexpected error occurred. Please try again.');
        }
    }  
  };

  export const postLoginToken = async idToken => {
    const API_URL = process.env.REACT_APP_API_URL;
    const path = '/v1/oauth/login';

    try {
      const response = await fetch(`${API_URL}${path}`, {
        method: 'POST',
        credentials: 'include', // include, *same-origin, omit
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(idToken), // body의 데이터 유형은 반드시 "Content-Type" 헤더와 일치해야 함
      });
      if (!response.ok)             throw new Error('An unexpected error occurred. Please try again.');
    } catch (e) {
      throw new Error(e.message)
    }
  };
